mapper.select:
SELECT a.bid_no, a.bid_job, a.bid_type, a.bid_name, a.bid_org, a.demand_org, a.contact , TO_CHAR( TO_TIMESTAMP(a.reg_date, 'YYYYMMDDHH24MI'), 'YYYY-MM-DD HH24:MI') as reg_date , TO_CHAR( TO_TIMESTAMP(a.bid_end_date, 'YYYYMMDDHH24MI'), 'YYYY-MM-DD HH24:MI') as bid_end_date , a.crawling_date, a.detail_url , b.e_method, b.open_yn, b.amount, b.presumed_value, b.budget, b.limited_region, b.possible_region, b.bidding_strategy_yn FROM tb_bid a LEFT OUTER JOIN tb_bid_detail b ON a.bid_no = b.bid_no
ORDER BY ${sortdatafield} ${sortorder}
ORDER BY bid_end_date DESC
LIMIT #{pagesize} OFFSET #{recordstartindex}

mapper.insert:
	INSERT INTO tb_bid (
		bid_no, bid_job, bid_type, bid_name, bid_org, demand_org, contact, reg_date, bid_end_date, crawling_date, detail_url
	) 
	VALUES 

    (
          #{item.bidNo}
        , #{item.bidJob}
        , #{item.bidType}
        , #{item.bidName}
        , #{item.bidOrg}
        , #{item.demandOrg}
        , #{item.contact}
        , #{item.regDate}
        , #{item.bidEndDate}
        , now()	
        , #{item.detailUrl}
    )

	INSERT INTO tb_bid_detail (
		bid_no, e_method, open_yn, amount, presumed_value, budget, limited_region, possible_region, bidding_strategy_yn, crawling_date
	) 
	VALUES 

    (
          #{item.bidNo}
        , #{item.eMethod}
        , #{item.openYn}
        , #{item.amount}
        , #{item.presumedValue}
        , #{item.budget}
        , #{item.limitedRegion}
        , #{item.possibleRegion}
        , #{item.biddingStrategyYn}
        , now()	
    )
